import { useState } from 'react'

const Tasks = ()  => {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            text: 'Doc Appointment',
            day: 'Feb 5 at 2 PM',
            reminder: true,
        },
        {
            id: 2,
            text: 'Meeting at School',
            day: 'Feb 7 at 1 PM',
            reminder: false,
        }  
    ])

    return (
        <>
            {tasks.map((task)=> (
                <h3>{task.text}</h3>
            ))}
        </>
    )
}

export default Tasks
